<?phpabstract class C_Base extends Controller{	protected $title;			protected $content;		protected $head;	protected $header;			function __construct()	{				$this->title = 'MyShop';		$this->content;		$this->head = '';		$this->header = '';	}		public function render()	{		$vars = array('title' => $this->title, 'content' => $this->content, 'head'=> $this->head, 'header'=>$this->header);			$page = $this->Template('v_main.tmpl', $vars);						echo $page;	}	}