<?phpabstract class Controller{		abstract function render();		protected function IsGet()	{		return $_SERVER['REQUEST_METHOD'] == 'GET';	}	protected function IsPost()	{		return $_SERVER['REQUEST_METHOD'] == 'POST';	}protected function Template($fileName, $arr){		$loader = new Twig_Loader_Filesystem('./../theme');    		$twig = new Twig_Environment($loader);  		$twig->addGlobal('session', $_SESSION);  		$template = $twig->loadTemplate($fileName);  	 	return $content = $template->render(array (    		'data' => $arr  			));	}		public function __call($name, $params){        die('url не найден!!!');	}}